contracts/DwellaReit.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.9;

import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC1155/ERC1155Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC1155/utils/ERC1155HolderUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/CountersUpgradeable.sol";
import '@openzeppelin/contracts-upgradeable/interfaces/IERC165Upgradeable.sol';
import "@openzeppelin/contracts-upgradeable/token/ERC1155/utils/ERC1155ReceiverUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/security/PausableUpgradeable.sol";
import '@openzeppelin/contracts/interfaces/IERC20.sol';

contract DR is Initializable, AccessControlUpgradeable, PausableUpgradeable, ERC1155Upgradeable, ERC1155HolderUpgradeable {

    /**
     * @dev Declaration of variables.
     * 1. `MODERATOR` - Group of addresses possessing moderator authorization for AccessControl contract
     * 2. `ADMIN_ROLE` - Group of addresses possessing admin authorization
     * 3. `MINTER_ROLE` - Group of addresses possessing minting authorization
     * 4. `_legalContracts` - Mapping of Token ID to Legal Contract File Hash
     * 5. `platform_address` - Main address for the platform owner
     */
    bytes32 public constant MODERATOR = keccak256("MODERATOR");
    bytes32 public constant ADMIN_ROLE = keccak256("ADMIN_ROLE");
    bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");
    mapping (string => uint256) _legalContracts;
    address public platform_address;

    /**
     * @dev Throws if called by any account without ADMIN_ROLE access.
     */
    modifier onlyAdmin() {
        require(hasRole(ADMIN_ROLE, msg.sender), "Access Denied: Caller is not the Admin");
        _;
    }

    /**
     * @dev Declaration of `_tokenID` variable as unique Token ID using Counters library
     */
    using CountersUpgradeable for CountersUpgradeable.Counter;
    CountersUpgradeable.Counter internal _tokenIds;
    uint256 public _tokenID;

    /**
     * @dev Declaring `DRToken` struct to store token details and
     * mapping of Token ID to DRToken Struct
     */
    struct DRToken {
        uint256 totalSupply;
        address owner;
        string valuationReport;
        string legalContract;
        uint256 fee;
        uint256 rate;
    }
    mapping (uint256 => DRToken) drToken;

     /**
     * @dev Declaration of new Event to record created token details for DRToken
     *
     * @param timestamp - Time Stamp of Event
     * @param id - Unique token ID
     * @param totalSupply - Number of tokens for unique token ID
     * @param owner - Asset Owner wallet address
     * @param valuationReport - File Hash of Valuation Report
     * @param legalContract - File Hash of Legal Contract
     */
    event DRTokenID(uint256 timestamp, uint256 indexed id, uint256 indexed totalSupply, address indexed owner, string valuationReport, string legalContract, uint256 rate);

     /**
     * @dev Declaration of new Event to record ERC20 token withdraw
     *
     * @param timestamp - Time Stamp of Event
     * @param initiator - Wallet Address that invoked the withdrawal process
     * @param recipient - Wallet address which received the USDT
     * @param amount - Total amount of USDT withdrawn
     */
    event TokenWithdrawn(uint256 timestamp, address initiator, address recipient, uint256 amount);

    /**
     * @dev Functions for ERC1155 Token
     * 1. __ERC1155_init - Construct the ERC1155 token with URI
     * 2. incrementTokenId() - Increase `tokenID` by 1 before minting new token
     */
    function __DR_init(address _platformAddress) initializer external {
        __ERC1155_init("https://token-cdn-domain/{id}.json");
        __DR_init_unchained(_platformAddress);
    }

    function __DR_init_unchained(address _platformAddress) initializer internal {
        platform_address = _platformAddress;
        _tokenID = _tokenIds.current();
    }

    function incrementTokenId() internal {
        _tokenIds.increment();
        _tokenID = _tokenIds.current();
    }

    function supportsInterface(bytes4 interfaceId) public view override(AccessControlUpgradeable, ERC1155ReceiverUpgradeable, ERC1155Upgradeable) returns (bool) {
        return super.supportsInterface(interfaceId);
    }

    /**
     * @dev Series of functions to retrieve token details from `DRToken` struct, including
     * Total Supply, Owner, Valuation Report, Legal Contract and Service Fee.
     *
     * @param tokenId - Unique token id of token
     */
    function getTotalSupply(uint256 tokenId) external view returns (uint256) {
        DRToken memory token = drToken[tokenId];
        return token.totalSupply;
    }

    function getOwner(uint256 tokenId) external view returns (address) {
        DRToken memory token = drToken[tokenId];
        return token.owner;
    }

    function getValuationRpt(uint256 tokenId) external view returns (string memory) {
        DRToken memory token = drToken[tokenId];
        return token.valuationReport;
    }

    function getLegalContr(uint256 tokenId) external view returns (string memory) {
        DRToken memory token = drToken[tokenId];
        return token.legalContract;
    }

    function getFee(uint256 tokenId) public view returns (uint256) {
        DRToken memory token = drToken[tokenId];
        return token.fee;
    }

    function getRate(uint256 tokenId) public view returns (uint256) {
        DRToken memory token = drToken[tokenId];
        return token.rate;
    }

    /**
     * @dev Creates `totalAmt` tokens of token type `newTokenId`, by calling ERC1155 _mint function.
     *
     * Can only be called by the current admin.
     * 
     * Emits a {DRTokenID} event.
     * Emits {TransferSingle} event via ERC1155 library.
     *
     * Requirements:
     * - `legalContr` must not have been used for another token ID.
     *
     * @param assetOwner - Asset Owner wallet address
     * @param totalAmt - Total number of tokens for unique token ID
     * @param valueRpt - File Hash of Valuation Report
     * @param legalContr - File Hash of Legal Contract
     * @param fee - Fees to be paid to platform owner for sale proceedings
     * @param rate - token rate for the sale
     */
    function mintToken(address assetOwner, uint256 totalAmt, string memory valueRpt, string memory legalContr, uint256 fee, uint256 rate) external onlyAdmin whenNotPaused {
        require(_legalContracts[legalContr] == 0, "This asset has already been tokenized.");
        require(totalAmt <= 50, 'Amounts exceeds limit');

        incrementTokenId();

        uint256 newTokenId = _tokenID;

        emit DRTokenID(block.timestamp, newTokenId, totalAmt, assetOwner, valueRpt, legalContr, rate);

        // Creates and updates DRToken Struct of unique token ID with token details
        DRToken storage token = drToken[_tokenID];
        token.totalSupply = totalAmt;
        token.owner = assetOwner;
        token.valuationReport = valueRpt;
        token.legalContract = legalContr;
        token.fee = fee;
        token.rate = rate;

        _legalContracts[legalContr] = _tokenID;

        _mint(platform_address, newTokenId, totalAmt, "");
    }

    /**
     * @dev Withdraws any ERC20 from Contract
     *
     * Can only be called by the current admin.
     * 
     * Emits a {TokenWithdrawn} event.
     *
     * @param _contract - token contact address to withdraw
     * @param _recipient - Wallet address to withdraw token to
     * @param _amount - Amount of ERC20 token to withdraw
     */
    function withdrawToken(address _contract, address _recipient, uint256 _amount) external onlyAdmin {
         IERC20 tokenContract = IERC20(_contract);
        // transfer the token from address of Catbotica address
        uint256 balance = tokenContract.balanceOf(address(this));
        require(balance >= _amount, "Amount of token withdrawn exceed balance.");

        emit TokenWithdrawn(block.timestamp, msg.sender, _recipient, _amount);

        tokenContract.transfer(_recipient, _amount);
    }

    /**
     * @dev Transfers `tokenAmt` tokens of token type `id` from `platform_address` to `investor`
     * by calling ERC1155 safeTransferFrom function.
     *
     * Emits a {TransferSingle} event via ERC1155 library.
     * 
     * Requirements:
     * - `id` must be equal or less than current Token ID.
     * - `tokenAmt` must be equal or less than balance of Proxy Contract
     *
     * Transfers the payable amount of ETH equivalent to amount of tokens multiplied by the token rate.
     * 
     * Requirements:
     * - Paid ETH amount must be equal or more than `tokenAmt` multiplied by the token rate.
     *
     * @param id - Token ID
     * @param tokenAmt - Number of tokens purchased
     */
    function buyToken(uint256 id, uint256 tokenAmt) external payable whenNotPaused {
        require(_tokenID >= id, "Token ID doesn't exist.");
        require(balanceOf(address(this), id) >= tokenAmt, "Number of tokens purchased exceed tokens available.");

        DRToken memory token = drToken[id];

        uint256 rate = token.rate;

        require(msg.value >= tokenAmt*rate, 'Insufficient ETH');

        super.safeTransferFrom(platform_address, msg.sender, id, tokenAmt, "0x0");
    }

}

