    /**
     * @dev Declaring `DRToken` struct to store token details and
     * mapping of Token ID to DRToken Struct
     */
    struct DRToken {
        uint256 totalSupply;
        address owner;
        string valuationReport;
        string legalContract;
        uint256 fee;
        uint256 rate;
    }
    mapping (uint256 => DRToken) drToken;

     /**
     * @dev Declaration of new Event to record created token details for DRToken
     *
     * @param timestamp - Time Stamp of Event
     * @param id - Unique token ID
     * @param totalSupply - Number of tokens for unique token ID
