     * @param valuationReport - File Hash of Valuation Report
     * @param legalContract - File Hash of Legal Contract
     */
    event DRTokenID(uint256 timestamp, uint256 indexed id, uint256 indexed totalSupply, address indexed owner, string valuationReport, string legalContract, uint256 rate);

