        __ERC1155_init("https://token-cdn-domain/{id}.json");
        __DR_init_unchained(_platformAddress);
    }

    function __DR_init_unchained(address _platformAddress) initializer internal {
        platform_address = _platformAddress;
        _tokenID = _tokenIds.current();
