     * @param _contract - token contact address to withdraw
     * @param _recipient - Wallet address to withdraw token to
     * @param _amount - Amount of ERC20 token to withdraw
     */
    function withdrawToken(address _contract, address _recipient, uint256 _amount) external onlyAdmin {
         IERC20 tokenContract = IERC20(_contract);
        // transfer the token from address of Catbotica address
        uint256 balance = tokenContract.balanceOf(address(this));
        require(balance >= _amount, "Amount of token withdrawn exceed balance.");

        emit TokenWithdrawn(block.timestamp, msg.sender, _recipient, _amount);

        tokenContract.transfer(_recipient, _amount);
    }

    /**
     * @dev Transfers `tokenAmt` tokens of token type `id` from `platform_address` to `investor`
     * by calling ERC1155 safeTransferFrom function.
     *
     * Emits a {TransferSingle} event via ERC1155 library.
     * 
     * Requirements:
     * - `id` must be equal or less than current Token ID.
     * - `tokenAmt` must be equal or less than balance of Proxy Contract
     *
     * Transfers the payable amount of ETH equivalent to amount of tokens multiplied by the token rate.
     * 
     * Requirements:
     * - Paid ETH amount must be equal or more than `tokenAmt` multiplied by the token rate.
     *
     * @param id - Token ID
     * @param tokenAmt - Number of tokens purchased
     */
    function buyToken(uint256 id, uint256 tokenAmt) external payable whenNotPaused {
        require(_tokenID >= id, "Token ID doesn't exist.");
        require(balanceOf(address(this), id) >= tokenAmt, "Number of tokens purchased exceed tokens available.");

        DRToken memory token = drToken[id];

        uint256 rate = token.rate;

        require(msg.value >= tokenAmt*rate, 'Insufficient ETH');

        super.safeTransferFrom(platform_address, msg.sender, id, tokenAmt, "0x0");
    }

